sys_script_include.script:
var NeedItUtils = Class.create();
NeedItUtils.prototype = {
        initialize: function() {},

        isDatePast: function(strDate) {

            //create GlideDateTime ojects for the current date and passed in date.

            var rightnow = new GlideDateTime();
            var testdate = new GlideDateTime(strDate);

            // 		if tested Date is before rightnow return true

            if (testdate.before(rightnow)) {
                return true;
            } else {
                return false;
            }

        },

        isDateToday: function(strDate) {

            // create GlideDateTime object for the current date and passed in date.
            var today = new GlideDateTime();

            var todayDate = today.getLocalDate();
            var passedDate = new GlideDateTime(strDate);

            // if tested Date is today return true.

            if ((todayDate.equals(passedDate.getLocalDate()))){
                    return true;
                } else {
                    return false;
                }
            },
            type: 'NeedItUtils'
        };
